import React from 'react';
import {CopyToClipboard} from 'react-copy-to-clipboard';

//Everything we sent from option param in app.js is received here as props.
const Options = (props) => {
    const {includeHtml,setIncludeHtml,setInputValue,setTag,tag,setCopiedCode,copiedCode,paragraphs} = props;
// Now we have access to all of the properties sent by App.js here in Options.js
    return (
        <div className="options">
            <div className="wrapper">
                <div className="optionsContainer">
                    <div className="paragraphs">
                        <p>Paragraphs :</p>
                        <input
                            type="number"
                            min="1"
                            max="50"
                            defaultValue = "1"
                            onChange={(e)=> setInputValue(e.target.value)}
                         />
                    </div>
                    <div className="tags">
                        <p>Tags :</p>
                        <select
                            defaultValue={tag}
                            onChange={(e)=>setTag(e.target.value)}
                        >
                            <option value="p">&lt;p&gt;</option>
                            <option value="h1">&lt;h1&gt;</option>
                            <option value="h2">&lt;h2&gt;</option>
                            <option value="h3">&lt;h3&gt;</option>
                            <option value="h4">&lt;h4&gt;</option>
                            <option value="h5">&lt;h5&gt;</option>
                            <option value="h6">&lt;h6&gt;</option>
                            <option value="span">&lt;span&gt;</option>
                        </select>
                    </div>
                    <div className="include">
                        <p>Include HTML :</p>
                        <select
                            defaultValue={includeHtml}
                            onChange={(e)=>setIncludeHtml(e.target.value)}
                        >
                        <option value="Yes">Yes</option>
                        <option value="No">No</option>
                        </select>
                    </div>

                </div>
                <div className="copy">
                     <CopyToClipboard>
                          <button>{copiedCode?"Copied Successfully":"Copy to Clipboard"}</button>
                     </CopyToClipboard>
                </div>
            </div>
        </div>
    )
}

export default Options;


// &lt; for left arrow and &gt; for right arrow.
// Input element above is given minimum and maximum through the element attributes.
// onChange event is very important above.
